/**
 * æ ¹æ“šæª”æ¡ˆåç¨±å–å¾— MIME Type
 * @param filename - æª”æ¡ˆåç¨±
 * @returns MIME Type
 */
function getMimeType(filename: string): string {
  const extension = filename.split('.').pop()?.toLowerCase()
  return extension
    ? MIME_TYPES[extension] || 'application/octet-stream'
    : 'application/octet-stream'
}

/**
 * å¾ Content-Disposition Header ä¸­æå–æª”æ¡ˆåç¨±
 * @param contentDisposition - Content-Disposition Header å€¼
 * @returns æª”æ¡ˆåç¨±
 */
function extractFilenameFromHeader(contentDisposition: string | null): string | null {
  if (!contentDisposition) return null

  // è™•ç† filename*=UTF-8''encoded-filename æ ¼å¼
  const filenameStarMatch = contentDisposition.match(/filename\*=UTF-8''(.+?)(?:;|$)/i)
  if (filenameStarMatch) {
    return decodeURIComponent(filenameStarMatch[1])
  }

  // è™•ç† filename="filename" æ ¼å¼
  const filenameMatch = contentDisposition.match(/filename="?(.+?)"?(?:;|$)/i)
  if (filenameMatch) {
    return filenameMatch[1]
  }

  return null
}

/**
 * ä½¿ç”¨ Blob ä¸‹è¼‰æª”æ¡ˆ
 * @param blob - Blob ç‰©ä»¶
 * @param filename - æª”æ¡ˆåç¨±
 */
function downloadBlob(blob: Blob, filename: string): void {
  // å»ºç«‹è‡¨æ™‚ URL
  const url = window.URL.createObjectURL(blob)

  // å»ºç«‹éš±è—çš„ <a> å…ƒç´ 
  const link = document.createElement('a')
  link.href = url
  link.download = filename
  link.style.display = 'none'

  // è§¸ç™¼ä¸‹è¼‰
  document.body.appendChild(link)
  link.click()

  // æ¸…ç†
  document.body.removeChild(link)
  window.URL.revokeObjectURL(url)
}

/**
 * æª”æ¡ˆä¸‹è¼‰ Composable
 * @returns æª”æ¡ˆä¸‹è¼‰ç›¸é—œæ–¹æ³•
 */
export function useFileDownload() {
  const notify = useNotify()
  const loading = useLoading()

  /**
   * å¾ URL ä¸‹è¼‰æª”æ¡ˆ
   * @param url - æª”æ¡ˆ URL
   * @param options - ä¸‹è¼‰é¸é …
   */
  const downloadFromUrl = async (url: string, options: FileDownloadOptions = {}): Promise<void> => {
    const {
      filename,
      globalLoading = false,
      loadingRef,
      onSuccess,
      onError,
      autoSuccess = true,
      autoError = true
    } = options

    try {
      // é–‹å§‹ Loading
      if (globalLoading) loading.start()
      if (loadingRef) loadingRef.value = true

      // ç™¼é€è«‹æ±‚
      const response = await fetch(url)

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`)
      }

      // å–å¾— Blob
      const blob = await response.blob()

      // æ±ºå®šæª”æ¡ˆåç¨±
      let finalFilename = filename
      if (!finalFilename) {
        // å˜—è©¦å¾ Header å–å¾—æª”æ¡ˆåç¨±
        const contentDisposition = response.headers.get('Content-Disposition')
        finalFilename = extractFilenameFromHeader(contentDisposition) || 'download'
      }

      // ä¸‹è¼‰æª”æ¡ˆ
      downloadBlob(blob, finalFilename)

      // æˆåŠŸå›å‘¼
      if (autoSuccess) {
        notify.success(`æª”æ¡ˆ ${finalFilename} ä¸‹è¼‰æˆåŠŸ`)
      }
      onSuccess?.(finalFilename)
    } catch (error) {
      // éŒ¯èª¤è™•ç†
      const err = error as Error
      if (autoError) {
        notify.error(`æª”æ¡ˆä¸‹è¼‰å¤±æ•—: ${err.message}`)
      }
      onError?.(err)
      throw error
    } finally {
      // çµæŸ Loading
      if (globalLoading) loading.finish()
      if (loadingRef) loadingRef.value = false
    }
  }

  /**
   * å¾ API ä¸‹è¼‰æª”æ¡ˆï¼ˆä½¿ç”¨ $fetchï¼‰
   * @param apiUrl - API è·¯å¾‘
   * @param options - ä¸‹è¼‰é¸é …
   */
  const downloadFromApi = async (
    apiUrl: string,
    options: FileDownloadOptions & { method?: 'GET' | 'POST'; body?: any } = {}
  ): Promise<void> => {
    const {
      filename,
      globalLoading = false,
      loadingRef,
      onSuccess,
      onError,
      autoSuccess = true,
      autoError = true,
      method = 'GET',
      body
    } = options

    const config = useRuntimeConfig()

    try {
      // é–‹å§‹ Loading
      if (globalLoading) loading.start()
      if (loadingRef) loadingRef.value = true

      // ç™¼é€ API è«‹æ±‚
      const blob = await $fetch<Blob>(apiUrl, {
        baseURL: config.public.api.baseUrl as string,
        method,
        body,
        responseType: 'blob'
      })

      // æ±ºå®šæª”æ¡ˆåç¨±
      const finalFilename = filename || 'download'

      // ä¸‹è¼‰æª”æ¡ˆ
      downloadBlob(blob, finalFilename)

      // æˆåŠŸå›å‘¼
      if (autoSuccess) {
        notify.success(`æª”æ¡ˆ ${finalFilename} ä¸‹è¼‰æˆåŠŸ`)
      }
      onSuccess?.(finalFilename)
    } catch (error) {
      // éŒ¯èª¤è™•ç†
      const err = error as Error
      if (autoError) {
        notify.error(`æª”æ¡ˆä¸‹è¼‰å¤±æ•—: ${err.message}`)
      }
      onError?.(err)
      throw error
    } finally {
      // çµæŸ Loading
      if (globalLoading) loading.finish()
      if (loadingRef) loadingRef.value = false
    }
  }

  /**
   * å¾ Base64 å­—ä¸²ä¸‹è¼‰æª”æ¡ˆ
   * @param base64 - Base64 å­—ä¸²ï¼ˆå¯åŒ…å«æˆ–ä¸åŒ…å« data URI å‰ç¶´ï¼‰
   * @param filename - æª”æ¡ˆåç¨±
   * @param options - ä¸‹è¼‰é¸é …
   */
  const downloadFromBase64 = (
    base64: string,
    filename: string,
    options: Omit<FileDownloadOptions, 'filename'> = {}
  ): void => {
    const { onSuccess, onError, autoSuccess = true, autoError = true } = options

    try {
      // ç§»é™¤ data URI å‰ç¶´ï¼ˆå¦‚æœæœ‰çš„è©±ï¼‰
      const base64Data = base64.includes(',') ? base64.split(',')[1] : base64

      // è§£ç¢¼ Base64
      const binaryString = window.atob(base64Data)
      const bytes = new Uint8Array(binaryString.length)
      for (let i = 0; i < binaryString.length; i++) {
        bytes[i] = binaryString.charCodeAt(i)
      }

      // å»ºç«‹ Blob
      const mimeType = getMimeType(filename)
      const blob = new Blob([bytes], { type: mimeType })

      // ä¸‹è¼‰æª”æ¡ˆ
      downloadBlob(blob, filename)

      // æˆåŠŸå›å‘¼
      if (autoSuccess) {
        notify.success(`æª”æ¡ˆ ${filename} ä¸‹è¼‰æˆåŠŸ`)
      }
      onSuccess?.(filename)
    } catch (error) {
      // éŒ¯èª¤è™•ç†
      const err = error as Error
      if (autoError) {
        notify.error(`æª”æ¡ˆä¸‹è¼‰å¤±æ•—: ${err.message}`)
      }
      onError?.(err)
      throw error
    }
  }

  /**
   * å¾ Blob ä¸‹è¼‰æª”æ¡ˆ
   * @param blob - Blob ç‰©ä»¶
   * @param filename - æª”æ¡ˆåç¨±
   * @param options - ä¸‹è¼‰é¸é …
   */
  const downloadFromBlob = (
    blob: Blob,
    filename: string,
    options: Omit<FileDownloadOptions, 'filename'> = {}
  ): void => {
    const { onSuccess, onError, autoSuccess = true, autoError = true } = options

    try {
      // ä¸‹è¼‰æª”æ¡ˆ
      downloadBlob(blob, filename)

      // æˆåŠŸå›å‘¼
      if (autoSuccess) {
        notify.success(`æª”æ¡ˆ ${filename} ä¸‹è¼‰æˆåŠŸ`)
      }
      onSuccess?.(filename)
    } catch (error) {
      // éŒ¯èª¤è™•ç†
      const err = error as Error
      if (autoError) {
        notify.error(`æª”æ¡ˆä¸‹è¼‰å¤±æ•—: ${err.message}`)
      }
      onError?.(err)
      throw error
    }
  }

  /**
   * å¾æ–‡å­—å…§å®¹å»ºç«‹ä¸¦ä¸‹è¼‰æª”æ¡ˆ
   * @param content - æ–‡å­—å…§å®¹
   * @param filename - æª”æ¡ˆåç¨±
   * @param options - ä¸‹è¼‰é¸é …
   */
  const downloadFromText = (
    content: string,
    filename: string,
    options: Omit<FileDownloadOptions, 'filename'> = {}
  ): void => {
    const mimeType = getMimeType(filename)
    const blob = new Blob([content], { type: mimeType })
    downloadFromBlob(blob, filename, options)
  }

  /**
   * å¾ JSON ç‰©ä»¶å»ºç«‹ä¸¦ä¸‹è¼‰æª”æ¡ˆ
   * @param data - JSON ç‰©ä»¶
   * @param filename - æª”æ¡ˆåç¨±ï¼ˆé è¨­ç‚º data.jsonï¼‰
   * @param options - ä¸‹è¼‰é¸é …
   */
  const downloadFromJson = (
    data: any,
    filename: string = 'data.json',
    options: Omit<FileDownloadOptions, 'filename'> = {}
  ): void => {
    const content = JSON.stringify(data, null, 2)
    downloadFromText(content, filename, options)
  }

  return {
    downloadFromUrl,
    downloadFromApi,
    downloadFromBase64,
    downloadFromBlob,
    downloadFromText,
    downloadFromJson
  }
}

/**
 * æª”æ¡ˆä¸Šå‚³ Composable
 * æ”¯æ´å–®æª”ã€å¤šæª”ä¸Šå‚³ï¼Œä»¥åŠå„ç¨®æª”æ¡ˆé¡å‹é©—è­‰
