 */
const FILE_TYPE_GROUPS: Record<string, string[]> = {
  image: ['image/jpeg', 'image/png', 'image/gif', 'image/bmp', 'image/webp', 'image/svg+xml'],
  document: [
    'application/pdf',
    'application/msword',
    'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
  ],
  spreadsheet: [
    'application/vnd.ms-excel',
    'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
    'text/csv'
  ],
  video: ['video/mp4', 'video/mpeg', 'video/quicktime', 'video/x-msvideo'],
  audio: ['audio/mpeg', 'audio/wav', 'audio/ogg'],
  archive: ['application/zip', 'application/x-rar-compressed', 'application/x-7z-compressed']
}

/**
 * æ ¼å¼åŒ–æª”æ¡ˆå¤§å°
 * @param bytes - ä½å…ƒçµ„æ•¸
 * @returns æ ¼å¼åŒ–å¾Œçš„å­—ä¸²
 */
function formatFileSize(bytes: number): string {
  if (bytes === 0) return '0 Bytes'
  const k = 1024
  const sizes = ['Bytes', 'KB', 'MB', 'GB']
  const i = Math.floor(Math.log(bytes) / Math.log(k))
  return `${Math.round((bytes / Math.pow(k, i)) * 100) / 100} ${sizes[i]}`
}

/**
 * é©—è­‰æª”æ¡ˆ
 * @param file - æª”æ¡ˆç‰©ä»¶
 * @param options - é©—è­‰é¸é …
 * @returns é©—è­‰çµæœ
 */
function validateFile(
  file: File,
  options: FileValidationOptions = {}
): { valid: boolean; error?: string } {
  const { maxSize, accept } = options

  // æª¢æŸ¥æª”æ¡ˆå¤§å°
  if (maxSize && file.size > maxSize) {
    return {
      valid: false,
      error: `æª”æ¡ˆå¤§å°è¶…éé™åˆ¶ï¼ˆ${formatFileSize(file.size)} > ${formatFileSize(maxSize)}ï¼‰`
    }
  }

  // æª¢æŸ¥æª”æ¡ˆé¡å‹
  if (accept && accept.length > 0) {
    const fileType = file.type
    const fileName = file.name
    const fileExt = `.${fileName.split('.').pop()?.toLowerCase()}`

    const isAccepted = accept.some((acceptType) => {
      // æª¢æŸ¥ MIME type
      if (acceptType.includes('/')) {
        // æ”¯æ´è¬ç”¨å­—å…ƒï¼Œä¾‹å¦‚ image/*
        if (acceptType.endsWith('/*')) {
          const category = acceptType.split('/')[0]
          return fileType.startsWith(`${category}/`)
        }
        return fileType === acceptType
      }
      // æª¢æŸ¥å‰¯æª”å
      return fileExt === acceptType.toLowerCase()
    })

    if (!isAccepted) {
      return {
        valid: false,
        error: `ä¸æ”¯æ´çš„æª”æ¡ˆé¡å‹ï¼ˆ${fileType || fileExt}ï¼‰`
      }
    }
  }

  return { valid: true }
}

/**
 * é©—è­‰å¤šå€‹æª”æ¡ˆ
 * @param files - æª”æ¡ˆé™£åˆ—
 * @param options - é©—è­‰é¸é …
 * @returns é©—è­‰çµæœ
 */
function validateFiles(
  files: File[],
  options: FileValidationOptions = {}
): { valid: boolean; error?: string } {
  const { minFiles, maxFiles } = options

  // æª¢æŸ¥æª”æ¡ˆæ•¸é‡
  if (minFiles && files.length < minFiles) {
    return {
      valid: false,
      error: `è‡³å°‘éœ€è¦ ${minFiles} å€‹æª”æ¡ˆ`
    }
  }

  if (maxFiles && files.length > maxFiles) {
    return {
      valid: false,
      error: `æœ€å¤šåªèƒ½ä¸Šå‚³ ${maxFiles} å€‹æª”æ¡ˆ`
    }
  }

  // é€ä¸€é©—è­‰æ¯å€‹æª”æ¡ˆ
  for (const file of files) {
    const result = validateFile(file, options)
    if (!result.valid) {
      return result
    }
  }

  return { valid: true }
}

/**
 * æª”æ¡ˆä¸Šå‚³ Composable
 * @returns æª”æ¡ˆä¸Šå‚³ç›¸é—œæ–¹æ³•
 */
export function useFileUpload() {
  const notify = useNotify()
  const loading = useLoading()
  const config = useRuntimeConfig()

  /**
   * ä¸Šå‚³å–®å€‹æª”æ¡ˆ
   * @param file - æª”æ¡ˆç‰©ä»¶
   * @param options - ä¸Šå‚³é¸é …
   * @returns ä¸Šå‚³çµæœ
   */
  const uploadFile = async (file: File, options: FileUploadOptions = {}): Promise<UploadResult> => {
    const {
      endpoint = '/api/upload',
      method = 'POST',
      fieldName = 'file',
      data = {},
      isGlobalLoading = false,
      loadingRef,
      // onProgress,
      onSuccess,
      onError,
      shouldAutoSuccess = true,
      shouldAutoError = true,
      maxSize,
      accept
    } = options

    try {
      // é–‹å§‹ Loading
      if (isGlobalLoading) loading.start()
      if (loadingRef) loadingRef.value = true

      // é©—è­‰æª”æ¡ˆ
      const validation = validateFile(file, { maxSize, accept })
      if (!validation.valid) {
        throw new Error(validation.error)
      }

      // å»ºç«‹ FormData
      const formData = new FormData()
      formData.append(fieldName, file)

      // é™„åŠ é¡å¤–è³‡æ–™
      Object.keys(data).forEach((key) => {
        formData.append(key, data[key])
      })

      // ç™¼é€è«‹æ±‚
      const response = await $fetch(endpoint, {
        baseURL: config.public.api.baseUrl as string,
        method,
        body: formData
      })

      // æˆåŠŸå›å‘¼
      if (shouldAutoSuccess) {
        notify.success(`æª”æ¡ˆ ${file.name} ä¸Šå‚³æˆåŠŸ`)
      }
      onSuccess?.(response)

      return {
        success: true,
        data: response
      }
    } catch (error) {
      // éŒ¯èª¤è™•ç†
      const err = error as Error
      if (shouldAutoError) {
        notify.error(`æª”æ¡ˆä¸Šå‚³å¤±æ•—: ${err.message}`)
      }
      onError?.(err)

      return {
        success: false,
        error: err.message
      }
    } finally {
      // çµæŸ Loading
      if (isGlobalLoading) loading.finish()
      if (loadingRef) loadingRef.value = false
    }
  }

  /**
   * ä¸Šå‚³å¤šå€‹æª”æ¡ˆ
   * @param files - æª”æ¡ˆé™£åˆ—
   * @param options - ä¸Šå‚³é¸é …
   * @returns ä¸Šå‚³çµæœé™£åˆ—
   */
  const uploadFiles = async (
    files: File[],
    options: FileUploadOptions = {}
  ): Promise<UploadResult[]> => {
    const {
      endpoint = '/api/upload/multiple',
      method = 'POST',
      fieldName = 'files',
      data = {},
      isGlobalLoading = false,
      loadingRef,
      onSuccess,
      onError,
      shouldAutoSuccess = true,
      shouldAutoError = true,
      maxSize,
      accept,
      allowMultiple = true
    } = options

    try {
      // é–‹å§‹ Loading
      if (isGlobalLoading) loading.start()
      if (loadingRef) loadingRef.value = true

      // é©—è­‰æª”æ¡ˆ
      const validation = validateFiles(files, {
        maxSize,
        accept,
        maxFiles: multiple ? undefined : 1
      })
      if (!validation.valid) {
        throw new Error(validation.error)
      }

      // å»ºç«‹ FormData
      const formData = new FormData()
      files.forEach((file) => {
        formData.append(fieldName, file)
      })

      // é™„åŠ é¡å¤–è³‡æ–™
      Object.keys(data).forEach((key) => {
        formData.append(key, data[key])
      })

      // ç™¼é€è«‹æ±‚
      const response = await $fetch(endpoint, {
        baseURL: config.public.api.baseUrl as string,
        method,
        body: formData
      })

      // æˆåŠŸå›å‘¼
      if (shouldAutoSuccess) {
        notify.success(`æˆåŠŸä¸Šå‚³ ${files.length} å€‹æª”æ¡ˆ`)
      }
      onSuccess?.(response)

      return [
        {
          success: true,
          data: response
        }
      ]
    } catch (error) {
      // éŒ¯èª¤è™•ç†
      const err = error as Error
      if (shouldAutoError) {
        notify.error(`æª”æ¡ˆä¸Šå‚³å¤±æ•—: ${err.message}`)
      }
      onError?.(err)

      return [
        {
          success: false,
          error: err.message
        }
      ]
    } finally {
      // çµæŸ Loading
      if (isGlobalLoading) loading.finish()
      if (loadingRef) loadingRef.value = false
    }
  }

  /**
   * å¾ input[type="file"] å…ƒç´ ä¸Šå‚³
   * @param inputElement - input å…ƒç´ æˆ– event
   * @param options - ä¸Šå‚³é¸é …
   * @returns ä¸Šå‚³çµæœ
   */
  const uploadFromInput = async (
    inputElement: HTMLInputElement | Event,
    options: FileUploadOptions = {}
  ): Promise<UploadResult | UploadResult[]> => {
    // å–å¾— input å…ƒç´ 
    let input: HTMLInputElement
    if (inputElement instanceof Event) {
      input = inputElement.target as HTMLInputElement
    } else {
      input = inputElement
    }

    const files = Array.from(input.files || [])

    if (files.length === 0) {
      throw new Error('æœªé¸æ“‡ä»»ä½•æª”æ¡ˆ')
    }

    // å–®æª”æˆ–å¤šæª”ä¸Šå‚³
    if (files.length === 1) {
      return await uploadFile(files[0], options)
    } else {
      return await uploadFiles(files, options)
    }
  }

  /**
   * å¾ Base64 å­—ä¸²ä¸Šå‚³
   * @param base64 - Base64 å­—ä¸²
   * @param filename - æª”æ¡ˆåç¨±
   * @param options - ä¸Šå‚³é¸é …
