   * @param file - æª”æ¡ˆ
   * @param options - é¸é …
   * @returns é©—è­‰çµæœ
  const validate = (file: File, options: FileValidationOptions = {}) => validateFile(file, options)

  /**
   * é©—è­‰å¤šå€‹æª”æ¡ˆï¼ˆå…¬é–‹æ–¹æ³•ï¼‰
   * @param files - æª”æ¡ˆåˆ—è¡¨
   * @param options - é¸é …
   * @returns é©—è­‰çµæœ
  const validateMultiple = (files: File[], options: FileValidationOptions = {}) =>
    validateFiles(files, options)

  /**
   * å¾äº‹ä»¶ä¸­æå–æª”æ¡ˆï¼ˆæ”¯æ´ Input change èˆ‡ Drag dropï¼‰
   * @param event - Input Event æˆ– Drag Event
   * @returns æª”æ¡ˆé™£åˆ—
   */
  const getSelectedFiles = (event: Event | DragEvent): File[] => {
    let files: FileList | null = null

