    uploadFiles,
    uploadFromInput,
    uploadFromBase64,
    uploadFromBlob,
    validate,
    validateMultiple,
    // å·¥å…·æ–¹æ³•
    getSelectedFiles,
    formatFileSize,
    FILE_TYPE_GROUPS
  }
}

/**
 * å…¨åŸŸ Loading ç‹€æ…‹ç®¡ç† Composable
 * ç”¨æ–¼ç®¡ç† API è«‹æ±‚æˆ–å…¶ä»–éåŒæ­¥æ“ä½œçš„ Loading ç‹€æ…‹
 * æ¡ç”¨è¨ˆæ•¸å™¨æ¨¡å¼ï¼š
 * - start(): è¨ˆæ•¸ +1
 * - finish(): è¨ˆæ•¸ -1
 * - isLoading: ç•¶è¨ˆæ•¸ > 0 æ™‚ç‚º true
 */
export const useLoading = () => {
  // ä½¿ç”¨ useState è®“ç‹€æ…‹åœ¨å…¨åŸŸå…±äº« (å–ä»£ Pinia Store)
  const requestCount = useState<number>('loading_request_count', () => 0)

  /**
   * æ˜¯å¦æ­£åœ¨ Loading
   */
  const isLoading = computed(() => requestCount.value > 0)

  /**
   * é–‹å§‹ Loading (è¨ˆæ•¸ +1)
   */
  const start = () => {
    requestCount.value++
  }

  /**
   * çµæŸ Loading (è¨ˆæ•¸ -1)
   */
  const finish = () => {
    if (requestCount.value > 0) {
      requestCount.value--
    }
  }

  /**
   * å¼·åˆ¶é‡ç½® Loading (æ­¸é›¶)
   * ç”¨æ–¼ç™¼ç”Ÿä¾‹å¤–æˆ–éœ€è¦å¼·åˆ¶é—œé–‰æ™‚
   */
  const reset = () => {
    requestCount.value = 0
  }

  return {
    isLoading,
    start,
    finish,
    reset,
    count: computed(() => requestCount.value)
  }
}

