/**
 * ç­‰å¾… useFetch è³‡æ–™è¼‰å…¥å®Œæˆ
 *
 * @example
 * const { data, pending } = useRepository().dashboard.getStats()
 * await waitForData(pending)
 * console.log('è³‡æ–™å·²è¼‰å…¥:', data.value)
 *
 * @param pending - useFetch è¿”å›çš„ pending Ref
 * @param timeout - è¶…æ™‚æ™‚é–“ï¼ˆæ¯«ç§’ï¼‰ï¼Œé è¨­ 10 ç§’
 * @returns Promise
 */
export async function waitForData(pending: Ref<boolean>, timeout: number = 10000): Promise<void> {
  return new Promise((resolve, reject) => {
    // å¦‚æœå·²ç¶“è¼‰å…¥å®Œæˆï¼Œç›´æ¥è¿”å›
    if (!pending.value) {
      resolve()
      return
    }

    // ç›£è½ pending è®ŠåŒ–
    const unwatch = watch(pending, (isPending) => {
      if (!isPending) {
        unwatch()
        resolve()
      }
    })

    // è¶…æ™‚è™•ç†
    setTimeout(() => {
      unwatch()
      reject(new Error(`è³‡æ–™è¼‰å…¥è¶…æ™‚ (${timeout}ms)`))
    }, timeout)
  })
}

/**
 * ç­‰å¾…å¤šå€‹ API è³‡æ–™éƒ½è¼‰å…¥å®Œæˆ
 *
 * @example
 * const stats = useRepository().dashboard.getStats()
 * const activities = useRepository().dashboard.getActivities({ limit: 10 })
 *
 * await waitForAll([stats.pending, activities.pending])
 * console.log('æ‰€æœ‰è³‡æ–™éƒ½å·²è¼‰å…¥')
 *
 * @param pendingRefs - pending Ref é™£åˆ—
 * @param timeout - è¶…æ™‚æ™‚é–“ï¼ˆæ¯«ç§’ï¼‰ï¼Œé è¨­ 10 ç§’
 */
export async function waitForAll(
  pendingRefs: Ref<boolean>[],
