/**
 * useTableData - è¡¨æ ¼è³‡æ–™è¼‰å…¥ç®¡ç†
 *
 * ç”¨é€”ï¼šç®¡ç† Table çš„è³‡æ–™è¼‰å…¥é‚è¼¯
 * æ”¯æ´ Server-side èˆ‡ Client-side å…©ç¨®æ¨¡å¼
 */

export interface TableDataOptions {
  api?: string
  staticData?: any[]
  itemsPerPage?: number
}

export interface TableDataState {
  items: Ref<any[]>
  totalItems: Ref<number>
  loading: Ref<boolean>
  page: Ref<number>
  itemsPerPage: Ref<number>
  search: Ref<string>
  loadItems: (options: { page: number; itemsPerPage: number }) => Promise<void>
}

/**
 * ä½¿ç”¨è¡¨æ ¼è³‡æ–™ç®¡ç†
 *
 * @param options - è¨­å®šé¸é …
 * @returns è¡¨æ ¼è³‡æ–™ç‹€æ…‹èˆ‡è¼‰å…¥æ–¹æ³•
 *
 * @example
 * ```ts
 * // Server-side mode
 * const tableData = useTableData({ api: '/api/users' })
 *
 * // Client-side mode
 * const tableData = useTableData({ staticData: [...] })
 * ```
 */
export function useTableData(options: TableDataOptions = {}): TableDataState {
  const { api = '', staticData = [], itemsPerPage: initialItemsPerPage = 5 } = options

  // ç‹€æ…‹ç®¡ç†
  const items = ref<any[]>([])
  const totalItems = ref(0)
  const loading = ref(false)
  const page = ref(1)
  const itemsPerPage = ref(initialItemsPerPage)
  const search = ref('')

  /**
   * è¼‰å…¥è³‡æ–™
   * @param root0
   * @param root0.page
   * @param root0.itemsPerPage
   */
  const loadItems = async ({
    page: p,
    itemsPerPage: ipp
  }: {
    page: number
    itemsPerPage: number
  }) => {
    if (!api) {
      // Client-side mode: ä½¿ç”¨éœæ…‹è³‡æ–™
      items.value = staticData
      totalItems.value = staticData.length
      return
    }

    // Server-side mode: å¾ API è¼‰å…¥
    loading.value = true
    try {
      const result = await $fetch(api, {
        params: {
          page: p,
          itemsPerPage: ipp,
          q: search.value
