      })

      if (result) {
        // æ”¯æ´å›å‚³æ ¼å¼ { items: [], total: 100 } æˆ–ç›´æ¥å›å‚³ []
        if (Array.isArray(result)) {
          items.value = result
          totalItems.value = result.length
        } else {
          const res = result as any
          items.value = res.items || []
          totalItems.value = res.total || 0
        }
      }
    } catch (e) {
      console.error('Failed to load items', e)
      items.value = []
      totalItems.value = 0
    } finally {
      loading.value = false
    }
  }

  // ç›£è½æœå°‹è®Šæ›´
  let searchTimeout: ReturnType<typeof setTimeout>
  watch(search, () => {
    clearTimeout(searchTimeout)
    searchTimeout = setTimeout(() => {
      page.value = 1
    }, 500)
  })

  // åˆå§‹è¼‰å…¥
  onMounted(() => {
    if (!api) {
      items.value = staticData
      totalItems.value = staticData.length
    }
  })

  return {
    items,
    totalItems,
    loading,
    page,
    itemsPerPage,
    search,
    loadItems
  }
}

/**
 * @åŠŸèƒ½ æµ®æ°´å° Composable
 * @description åœ¨é é¢ä¸Šç¹ªè£½æµ®æ°´å°ï¼Œç”¨æ–¼è¿½æº¯è³‡æ–™ä¾†æº
 * @ç”¨æ³•
 * ```vue
 * <script setup>
 * const { showWatermark, hideWatermark } = useWatermark()
 *
 * onMounted(() => {
 *   showWatermark({ text: 'ä½¿ç”¨è€…å¸³è™Ÿ' })
 * })
 * </script>
 * ```
 */

interface WatermarkOptions {
  /** æµ®æ°´å°æ–‡å­—ï¼ˆé è¨­ï¼šä½¿ç”¨è€…å¸³è™Ÿ + æ™‚é–“ï¼‰ */
  text?: string
  /** å­—é«”å¤§å°ï¼ˆé è¨­ï¼š16ï¼‰ */
  fontSize?: number
  /** å­—é«”é¡è‰²ï¼ˆé è¨­ï¼šrgba(0, 0, 0, 0.08)ï¼‰ */
  color?: string
  /** æ—‹è½‰è§’åº¦ï¼ˆé è¨­ï¼š-25ï¼‰ */
  rotate?: number
  /** æ°´å¹³é–“è·ï¼ˆé è¨­ï¼š200ï¼‰ */
  gapX?: number
  /** å‚ç›´é–“è·ï¼ˆé è¨­ï¼š150ï¼‰ */
  gapY?: number
  /** z-indexï¼ˆé è¨­ï¼š9999ï¼‰ */
  zIndex?: number
}

/**
 *
 */
export function useWatermark() {
  const watermarkId = 'app-watermark'
  let watermarkEl: HTMLDivElement | null = null

  /**
   * å»ºç«‹æµ®æ°´å° Canvas
   * @param options
   */
  const createWatermarkCanvas = (options: Required<WatermarkOptions>): string => {
    const { text, fontSize, color, rotate, gapX, gapY } = options

    const canvas = document.createElement('canvas')
    const ctx = canvas.getContext('2d')!

    // è¨ˆç®—æ–‡å­—å¯¬åº¦ä»¥è¨­å®š canvas å°ºå¯¸
    ctx.font = `${fontSize}px Microsoft JhengHei, sans-serif`
    const textWidth = ctx.measureText(text).width

    // è¨­å®š canvas å°ºå¯¸ï¼ˆåŒ…å«é–“è·ï¼‰
    canvas.width = textWidth + gapX
    canvas.height = fontSize + gapY

    // è¨­å®šæ–‡å­—æ¨£å¼
    ctx.font = `${fontSize}px Microsoft JhengHei, sans-serif`
    ctx.fillStyle = color
    ctx.textAlign = 'center'
    ctx.textBaseline = 'middle'

    // æ—‹è½‰ä¸¦ç¹ªè£½æ–‡å­—
    ctx.translate(canvas.width / 2, canvas.height / 2)
    ctx.rotate((rotate * Math.PI) / 180)
    ctx.fillText(text, 0, 0)

    return canvas.toDataURL('image/png')
  }

  /**
   * é¡¯ç¤ºæµ®æ°´å°
   * @param options
   */
  const showWatermark = (options: WatermarkOptions = {}) => {
    // ç§»é™¤èˆŠçš„æµ®æ°´å°
    hideWatermark()

    // é è¨­å€¼
    const defaultOptions: Required<WatermarkOptions> = {
      text: options.text || `${new Date().toLocaleDateString()}`,
      fontSize: options.fontSize ?? 14,
      color: options.color ?? 'rgba(0, 0, 0, 0.06)',
      rotate: options.rotate ?? -25,
      gapX: options.gapX ?? 180,
      gapY: options.gapY ?? 120,
      zIndex: options.zIndex ?? 9999
    }

    // å»ºç«‹æµ®æ°´å°åœ–ç‰‡
    const watermarkImage = createWatermarkCanvas(defaultOptions)

    // å»ºç«‹æµ®æ°´å°å…ƒç´ 
    watermarkEl = document.createElement('div')
    watermarkEl.id = watermarkId
    watermarkEl.style.cssText = `
      position: fixed;
      top: 0;
      left: 0;
      width: 100vw;
      height: 100vh;
      pointer-events: none;
      z-index: ${defaultOptions.zIndex};
      background-image: url('${watermarkImage}');
      background-repeat: repeat;
    `

    document.body.appendChild(watermarkEl)

    // ä½¿ç”¨ MutationObserver é˜²æ­¢æµ®æ°´å°è¢«ç§»é™¤
    const observer = new MutationObserver((mutations) => {
      mutations.forEach((mutation) => {
        // å¦‚æœæµ®æ°´å°è¢«ç§»é™¤ï¼Œé‡æ–°åŠ å…¥
        if (mutation.type === 'childList') {
          const removed = Array.from(mutation.removedNodes).find(
            (node) => (node as HTMLElement).id === watermarkId
          )
          if (removed && watermarkEl) {
            document.body.appendChild(watermarkEl)
          }
        }

        // å¦‚æœæµ®æ°´å°å±¬æ€§è¢«ä¿®æ”¹ï¼Œé‡æ–°å»ºç«‹
        if (mutation.type === 'attributes' && (mutation.target as HTMLElement).id === watermarkId) {
          hideWatermark()
          showWatermark(options)
        }
      })
    })

    observer.observe(document.body, {
      childList: true,
      subtree: true,
      attributes: true,
      attributeFilter: ['style', 'class']
    })
  }

  /**
   * éš±è—æµ®æ°´å°
   */
  const hideWatermark = () => {
    const existing = document.getElementById(watermarkId)
    if (existing) {
      existing.remove()
    }
    watermarkEl = null
  }

  /**
   * æ›´æ–°æµ®æ°´å°æ–‡å­—
   * @param text
   */
  const updateWatermark = (text: string) => {
    hideWatermark()
    showWatermark({ text })
  }

  return {
    showWatermark,
    hideWatermark,
    updateWatermark
  }
}

