/**
 * Mock Data Interceptor (Simplified)
 *
 * ç®¡ç†æ‰€æœ‰ Mock API çš„è¨­å®šèˆ‡å›å‚³è³‡æ–™ã€‚
 * æ¡ç”¨ "Configuration over Code" æ¨¡å¼ï¼Œæ–¹ä¾¿å¿«é€Ÿæ–°å¢ã€‚
 */

// å®šç¾© Mock è·¯ç”±è¦å‰‡ä»‹é¢
import { MOCK_ROUTES } from '~/mock/routes'

// =============================================================================
// æ ¸å¿ƒé‚è¼¯ (ç„¡é ˆé »ç¹ä¿®æ”¹)
// =============================================================================

/**
 * æª¢æŸ¥æ˜¯å¦éœ€è¦å›å‚³ Mock è³‡æ–™
 *
 * @param url - è«‹æ±‚çš„ URL (å« query string)
 * @param options - è«‹æ±‚é¸é … (method, body ç­‰)
 * @returns Mock è³‡æ–™æˆ– null
 */
export async function checkMockData(url: string, options: any): Promise<any> {
  const config = useRuntimeConfig()

  // 1. å…¨åŸŸ Mock é–‹é—œæª¢æŸ¥ (è‹¥ä¾è³´ config)
  // é€™è£¡ä¿ç•™æ‚¨åŸæœ¬çš„è¨­è¨ˆï¼Œé‡å°ä¸åŒæ¨¡çµ„åšé–‹é—œæª¢æŸ¥
  // å¦‚æœæƒ³æ›´ç°¡åŒ–ï¼Œå¯ä»¥åªæª¢æŸ¥ featureApiMock æˆ–ç”šè‡³ç§»é™¤
  if (!isMockEnabled(url, config)) {
    return null
  }

  // 2. å°‹æ‰¾åŒ¹é…çš„è¦å‰‡
  const method = options.method || 'GET'
  // ç§»é™¤ query string é€²è¡ŒåŒ¹é…
  const pureUrl = url.split('?')[0]

  const route = MOCK_ROUTES.find((r) => {
    // æª¢æŸ¥ Method
    if (r.method !== method) return false

    // æª¢æŸ¥ URL
    if (r.url instanceof RegExp) {
      return r.url.test(pureUrl)
    }
    return r.url === pureUrl || pureUrl.includes(r.url as string) // å…¼å®¹ includes ä»¥é˜²è¬ä¸€
  })

  if (!route) {
    console.log('No Mock Route found for:', url)
    return null
  }

  console.log('Mock Hit:', url)

  // 3. æ¨¡æ“¬å»¶é²
  // 3. æ¨¡æ“¬å»¶é² (èˆ‡å‰ç‰ˆä¸åŒï¼Œé€™è£¡åªè¨ˆç®—å»¶é²æ™‚é–“ï¼Œä¸å¯¦éš›ç­‰å¾…ï¼Œäº¤ç”± useApi æ±ºå®šç­‰å¾…æ™‚æ©Ÿ)
  const delayTime = route.delay ?? (parseInt(config.public.mockDelay as string) || 500)

  // 4. ç”¢ç”Ÿå›æ‡‰
  let responseData
  if (typeof route.response === 'function') {
    const query = extractQueryParams(url)
    const body = options.body || {}
    responseData = route.response({ query, body, url })
  } else {
    responseData = route.response
  }

  return {
    data: responseData,
    delay: delayTime
  }
}

/**
 * è¼”åŠ©ï¼šåˆ¤æ–· Mock é–‹é—œ
 * @param url - è«‹æ±‚è·¯å¾‘
 * @param config - Runtime Config
 */
function isMockEnabled(url: string, config: any): boolean {
  // å„ªå…ˆæª¢æŸ¥ç‰¹å®š API çš„ Mock é–‹é—œ
  // è‹¥ç‰¹å®šé–‹é—œæœ‰è¨­å®š (ä¸ç‚º undefined)ï¼Œå‰‡ä»¥è©²è¨­å®šç‚ºæº–
  if (url.includes('/users')) {
    const val = config.public.mockUserApi
    if (val !== undefined && val !== '') return String(val) === 'true' || val === true
  }
  if (url.includes('/orders')) {
    const val = config.public.mockOrderApi
    if (val !== undefined && val !== '') return String(val) === 'true' || val === true
  }
  if (url.includes('/dashboard')) {
    const val = config.public.mockDashboardApi
    if (val !== undefined && val !== '') return String(val) === 'true' || val === true
  }

  // è‹¥ç„¡ç‰¹å®šè¨­å®šï¼Œå‰‡ä½¿ç”¨é è¨­å…¨åŸŸé–‹é—œ
  return config.public.featureApiMock === 'true' || config.public.featureApiMock === true
}

/**
 * è¼”åŠ©ï¼šè§£æ Query Params
 * @param url - å®Œæ•´è«‹æ±‚è·¯å¾‘
 */
function extractQueryParams(url: string): Record<string, string> {
  const params: Record<string, string> = {}
  const queryString = url.split('?')[1]
  if (!queryString) return params

  queryString.split('&').forEach((param) => {
    const [key, value] = param.split('=')
